CREATE TABLE IF NOT EXISTS ProductContainer ( id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, Name TEXT NOT NULL, ThreeMonthSupply REAL, parentID INTEGER, ThreeMonthSupplyType INTEGER );
CREATE TABLE IF NOT EXISTS Item ( id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, productID INTEGER NOT NULL, Barcode INTEGER NOT NULL, EntryDate INTEGER NOT NULL, ExitTime INTEGER, ExpirationDate INTEGER, containerID INTEGER NOT NULL  );
CREATE TABLE IF NOT EXISTS Product ( id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, CreationDate INTEGER NOT NULL, Barcode TEXT NOT NULL, Description TEXT NOT NULL, Size REAL NOT NULL, ShelfLife INTEGER, ThreeMonthSupply INTEGER, SizeUnit INTEGER NOT NULL );
CREATE TABLE IF NOT EXISTS ContainerProducts ( containerID INTEGER NOT NULL, productID INTEGER NOT NULL );
CREATE TABLE IF NOT EXISTS InventoryData ( id INTEGER NOT NULL PRIMARY KEY AUTOINCREMENT, key TEXT NOT NULL, value TEXT NOT NULL );